post_install:
"dir: $dir"
$import = "try { `import-module `"$dir\PSReadline.psd1`" } catch { }"

if($profile) {
    'adding PSReadLine to powershell profile...'
    if(!(test-path $profile)) {
        'powershell profile doesn''t exist. creating...'
        $profile_dir = split-path $profile
        if(!(test-path $profile_dir)) { mkdir $profile_dir > $null }
        '' > $profile
    }
    $text = get-content $profile
    if (($text | sls 'PSReadLine') -eq $null) {
        $new_profile = @($text) + $import
        $new_profile > $profile
        success 'PSReadLine added to powershell profile.'
    }
} else { warn 'no powershell profile found. the PSReadLine module will not be imported each session.' }

iex "$import"
